          <div style={{ textAlign: "center", padding: 60, color: "#999" }}>読み込み中...</div>
        ) : !data || data.totalResponses === 0 ? (
          <div style={{ textAlign: "center", padding: 60, color: "#999" }}>回答データがありません</div>
        ) : (
