const fs = require("fs");
const data = JSON.parse(fs.readFileSync("public/r/R2511/R2511A.json","utf8"));
const responses = data.responses;

// For Q8, the delimiters are between categories, not within them.
// The categories start with known prefixes. Let me use a regex approach.
// Categories: 画像生成(...)、音楽生成(...)、テキスト生成(...)、動画生成(...)、コーディング支援(...)
// Delimiter between categories: "), " followed by a category name

// Actually, look at the raw values - the items contain commas inside parentheses.
// The correct split: split on ", " but only when the next part starts with a known category keyword.

// Known Q8 category prefixes
const q8Categories = [
  "画像生成",
  "音楽生成",
  "テキスト生成",
  "動画生成",
  "コーディング支援",
  "3Dモデル生成"
];

function smartSplitQ8(val) {
  const items = [];
  let current = "";
  val.split(", ").forEach(part => {
    if (q8Categories.some(cat => part.startsWith(cat)) && current !== "") {
      items.push(current.trim());
      current = part;
    } else {
      current = current ? current + ", " + part : part;
    }
  });
  if (current) items.push(current.trim());
  return items;
}

// Q8 with smart split
const q8freq = {};
let q8empty = 0;
let q8respondents = 0;
responses.forEach(r => {
  const val = (r["Q8"] || "").trim();
  if (val === "") { q8empty++; return; }
  q8respondents++;
  const items = smartSplitQ8(val);
  items.forEach(item => {
    // Normalize: take just the category name (before the parenthesis) for short label
    q8freq[item] = (q8freq[item] || 0) + 1;
  });
});

console.log("=== Q8 (smart split) === respondents: " + q8respondents + ", empty: " + q8empty);
Object.entries(q8freq).sort((a,b) => b[1]-a[1]).forEach(([k,v]) => {
  console.log("  [" + v + "] " + k);
