const fs = require("fs");
const data = JSON.parse(fs.readFileSync("/Users/aki/git.local/u-aicujp/public/r/R2511/R2511A.json","utf8"));
const responses = data.responses;

// Smart split for Q8 - categories contain commas in parenthetical examples
const q8Categories = [
  "画像生成",
  "音楽生成",
  "テキスト生成",
  "動画生成",
  "コーディング支援",
  "3Dモデル生成"
];

function smartSplitQ8(val) {
  const items = [];
  let current = "";
  val.split(", ").forEach(part => {
    if (q8Categories.some(cat => part.startsWith(cat)) && current !== "") {
      items.push(current.trim());
      current = part;
    } else {
      current = current ? current + ", " + part : part;
    }
  });
  if (current) items.push(current.trim());
  return items;
}

// Q8
const q8freq = {};
let q8empty = 0;
let q8respondents = 0;
responses.forEach(r => {
  const val = (r["Q8"] || "").trim();
  if (val === "") { q8empty++; return; }
  q8respondents++;
  smartSplitQ8(val).forEach(item => {
    // Normalize to short category name
    const short = item.split("（")[0];
    q8freq[short] = (q8freq[short] || 0) + 1;
  });
});
console.log("=== Q8 (smart split, short labels) === respondents: " + q8respondents + ", empty: " + q8empty);
Object.entries(q8freq).sort((a,b) => b[1]-a[1]).forEach(([k,v]) => {
  console.log("  [" + v + "] " + k);
});

// Q13 smart split - categories with parenthetical content
const q13Categories = [
  "技術コミュニティ",
  "コミュニティ",
  "オンライン動画コンテンツ",
  "Web上の技術記事・ブログ",
  "書籍・電子書籍",
  "独学",
  "特に学んでいない",
  "無料イベントやセミナー",
  "大学院",
  "松尾・岩澤研究所講座",
  "Discordコミュニティ",
  "note",
  "当初は",
  "スクール"
];

function smartSplitQ13(val) {
  const items = [];
  let current = "";
  val.split(", ").forEach(part => {
    if (q13Categories.some(cat => part.startsWith(cat)) && current !== "") {
      items.push(current.trim());
      current = part;
    } else {
      current = current ? current + ", " + part : part;
    }
  });
  if (current) items.push(current.trim());
  return items;
}

const q13freq = {};
let q13empty = 0;
let q13respondents = 0;
responses.forEach(r => {
  const val = (r["Q13"] || "").trim();
  if (val === "") { q13empty++; return; }
  q13respondents++;
  smartSplitQ13(val).forEach(item => {
    q13freq[item] = (q13freq[item] || 0) + 1;
  });
});
console.log("\n=== Q13 (smart split) === respondents: " + q13respondents + ", empty: " + q13empty);
Object.entries(q13freq).sort((a,b) => b[1]-a[1]).forEach(([k,v]) => {
  console.log("  [" + v + "] " + k);
});

// Q15-Q23 (matrix: 現状 vs 今後)
console.log("\n=== Q15-Q23 (matrix pattern) ===");
for (let i = 15; i <= 23; i++) {
  const qKey = "Q" + i;
  let dekiteiru = 0;
  let nobashitai = 0;
  let both = 0;
  let empty = 0;
  let other = 0;
  responses.forEach(r => {
    const val = (r[qKey] || "").trim();
    if (val === "") { empty++; return; }
    const hasDeki = val.includes("現状「できている」");
    const hasNobu = val.includes("今後「伸ばしたい・期待したい」");
    if (hasDeki && hasNobu) { both++; }
    else if (hasDeki) { dekiteiru++; }
