const data = require('/Users/aki/git.local/u-aicujp/public/r/R2511/R2511A.json');
const responses = data.responses;
const total = responses.length; // 53

// Helper: count single-choice answers
function countSingle(key) {
  const counts = {};
  let answered = 0;
  responses.forEach(r => {
    const v = r[key];
    if (v && v.trim()) { counts[v] = (counts[v] || 0) + 1; answered++; }
  });
  return { counts, answered };
}

// Helper: count multi-choice (comma-separated)
function countMulti(key) {
  const counts = {};
  let answered = 0;
  responses.forEach(r => {
    const v = r[key];
    if (v && v.trim()) {
      answered++;
      v.split(', ').forEach(item => {
        const t = item.trim();
        if (t) counts[t] = (counts[t] || 0) + 1;
      });
    }
  });
  return { counts, answered };
}

// Helper: count matrix (できている vs 伸ばしたい)
function countMatrix(keys, labels) {
  const doneCounts = {};
  const wantCounts = {};
  let doneAnswered = 0, wantAnswered = 0;
  labels.forEach(l => { doneCounts[l] = 0; wantCounts[l] = 0; });
  keys.forEach((key, i) => {
    const label = labels[i];
    let hasDone = false, hasWant = false;
    responses.forEach(r => {
      const v = r[key];
      if (!v) return;
      if (v.includes('現状「できている」')) { doneCounts[label]++; hasDone = true; }
      if (v.includes('今後「伸ばしたい・期待したい」')) { wantCounts[label]++; hasWant = true; }
    });
    if (hasDone) doneAnswered++;
    if (hasWant) wantAnswered++;
  });
  // Count unique respondents who answered at least one
  let dA = 0, wA = 0;
  responses.forEach(r => {
    let anyDone = false, anyWant = false;
    keys.forEach(k => {
      const v = r[k];
      if (v && v.includes('現状')) anyDone = true;
      if (v && v.includes('今後')) anyWant = true;
    });
    if (anyDone) dA++;
    if (anyWant) wA++;
  });
  return { doneCounts, wantCounts, doneAnswered: dA, wantAnswered: wA };
}

