document.getElementById('meta').textContent=`回答数: ${data.totalResponses}件 / 最終更新: ${new Date(data.updatedAt).toLocaleString("ja-JP",{timeZone:"Asia/Tokyo"})}`;
document.getElementById('metrics-note').style.display='';

const isSample=true; // Always sample mode for static preview
const container=document.getElementById('content');
let html='';

const pairedDone=data.questions.find(q=>q.id===PAIRED_IDS.done);
const pairedWant=data.questions.find(q=>q.id===PAIRED_IDS.want);
const genderQ=data.questions.find(q=>q.id===GENDER_ID);
const ageQ=data.questions.find(q=>q.id===AGE_ID);
let pairedInserted=false,pyramidInserted=false;

for(const q of data.questions){
  if(q.id===PAIRED_IDS.want)continue;

  // Paired bar
  if(q.id===PAIRED_IDS.done&&pairedDone&&pairedWant&&!pairedInserted){
    const gaps=computeGapAnalysis(pairedDone.counts,pairedWant.counts,pairedDone.answered,pairedWant.answered);
    const cm=getPairedCommentary(gaps);
    html+=renderCard("AI利用の効果（実現 vs 期待）",`実現: ${pairedDone.answered}件 / 期待: ${pairedWant.answered}件の回答（複数選択可）`,
      renderPairedBar(pairedDone.counts,pairedWant.counts,pairedDone.answered,pairedWant.answered),cm,"",isSample);
    pairedInserted=true;continue;
  }

  const viz=VIZ_MAP[q.id]||"horizontal-bar";

  // Age pie
  if(viz==="age-pie"){
    const st=computeStats(q.counts,q.answered,data.totalResponses);
    const cm=getCommentary(q.id,q.counts,q.answered,data.totalResponses,st);
    html+=renderCard(q.question,`${q.answered}件の回答`,
      renderAgePie(data.birthYearCounts||{},q.answered),cm,statsHtml(st),isSample);
    // Pyramid right after
    if(!pyramidInserted&&genderQ&&ageQ&&data.pyramidData?.length){
      const pcm=getPyramidCommentary(data.pyramidData);
      html+=renderCard("回答者の年代×性別（人口ピラミッド）",`${data.pyramidData.length}件の回答`,
        renderPyramid(data.pyramidData,data.birthYearCounts||{},genderQ.counts),pcm,"",isSample);
      pyramidInserted=true;
    }
    continue;
  }

  const isMulti=q.type==="multi_choice";
  const st=computeStats(q.counts,q.answered,data.totalResponses);
  const cm=getCommentary(q.id,q.counts,q.answered,data.totalResponses,st);
  let chart="";

  if(viz==="stacked-bar") chart=renderStackedBar(q.counts,q.answered);
  else if(viz==="donut") chart=renderDonut(q.counts,q.answered);
  else if(viz==="regional-pie") chart=renderRegionalPie(q.counts,q.answered);
  else if(viz==="treemap") chart=renderTreemap(q.counts,q.answered);
  else if(viz==="tag-cloud") chart=renderTagCloud(q.counts,q.answered);
  else chart=renderHorizontalBar(q.counts,q.answered,isMulti);

  html+=renderCard(q.question,`${q.answered}件の回答${isMulti?"（複数選択可）":""}`,chart,cm,statsHtml(st),isSample);
}

container.innerHTML=html;

