function renderHorizontalBar(counts,answered,isMulti){
  const entries=Object.entries(counts).filter(([l,v])=>v>0&&!l.startsWith("─")).sort((a,b)=>b[1]-a[1]);
  if(!entries.length)return"<p>データなし</p>";
  const max=entries[0][1];let h='';
  entries.forEach(([l,c],i)=>{
    const w=max>0?c/max*100:0;
    const op=Math.max(.55,1-i*(isMulti?.06:.07));
    const col=isMulti?`rgba(0,49,216,${op})`:`rgba(65,201,180,${op})`;
    h+=`<div class="hbar-row"><span class="hbar-label">${esc(l)}</span><div class="hbar-track"><div class="hbar-fill" style="width:${Math.max(w,1)}%;background:${col}"></div></div><span class="hbar-val">${c} (${answered>0?Math.round(c/answered*100):0}%)</span></div>`;
  });
  return h;
}

