 107→// ─── Submit helpers ───
 108→async function submitSurvey(config, answers, email) {
 109→  // Google Form submission
 110→  if (config.submitToGoogleForm && config.resolvedUrl) {
 111→    const submitUrl = config.resolvedUrl.replace("/viewform", "/formResponse");
 112→    const params = new URLSearchParams();
 113→    // Track array values per entryId (for merging virtualEntries from multiple questions)
 114→    const entryArrays = {};
 115→    for (const q of config.questions) {
 116→      if (q.type === "section") continue;
 117→      const a = answers[q.id];
 118→      // Regular entryId mapping
 119→      if (q.entryId && a !== undefined && a !== null && a !== "") {
 120→        if (Array.isArray(a)) {
 121→          a.forEach((v) => params.append(`entry.${q.entryId}`, v));
 122→        } else {
 123→          params.set(`entry.${q.entryId}`, String(a));
 124→        }
 125→      }
 126→      // virtualEntries: derive additional Google Form entries
 127→      if (q.virtualEntries && a !== undefined && a !== null) {
 128→        for (const ve of q.virtualEntries) {
 129→          const derived = resolveDerive(ve.derive, a);
 130→          if (derived === null || derived === undefined) continue;
 131→          if (Array.isArray(derived)) {
 132→            if (!entryArrays[ve.entryId]) entryArrays[ve.entryId] = [];
 133→            entryArrays[ve.entryId].push(...derived);
 134→          } else {
 135→            params.set(`entry.${ve.entryId}`, String(derived));
 136→          }
 137→        }
 138→      }
 139→    }
 140→    // Append merged array entries
 141→    for (const [eid, values] of Object.entries(entryArrays)) {
 142→      values.forEach((v) => params.append(`entry.${eid}`, v));
 143→    }
 144→    const pageCount = config.questions.filter((q) => q.type === "section").length + 1;
 145→    params.set("pageHistory", Array.from({ length: pageCount }, (_, i) => i).join(","));
 146→    params.set("fvv", "1");
 147→    try {
 148→      await fetch(submitUrl, {
 149→        method: "POST",
 150→        mode: "no-cors",
 151→        headers: { "Content-Type": "application/x-www-form-urlencoded" },
 152→        body: params.toString(),
 153→      });
 154→    } catch { /* no-cors — ignore */ }
 155→  }
 156→  // Custom API submission (with retry)
 157→  if (config.submitUrl) {
 158→    const effectiveEmail = email || answers["entry_1243761143"] || undefined;
 159→    let finalAnswers = { ...answers };
 160→    // Apply mergedQuestions splits (declarative answer derivation for API)
 161→    if (config.mergedQuestions) {
 162→      for (const mq of config.mergedQuestions) {
 163→        const combined = finalAnswers[mq.questionId];
 164→        if (Array.isArray(combined)) {
 165→          for (const split of mq.splits) {
 166→            finalAnswers[split.answerId] = combined.filter((v) => split.options.includes(v));
 167→          }
 168→        }
 169→      }
 170→    }
 171→    // Filter out separator options (─────) from array answers
 172→    for (const [key, val] of Object.entries(finalAnswers)) {
 173→      if (Array.isArray(val)) {
 174→        finalAnswers[key] = val.filter((v) => typeof v !== "string" || !v.includes("─────"));
 175→      }
 176→    }
 177→    const payload = JSON.stringify({
 178→      surveyId: config.sourceUrl || config.title,
 179→      answers: finalAnswers,
 180→      submittedAt: new Date().toISOString(),
 181→      email: effectiveEmail,
 182→    });
 183→    let saved = false;
 184→    for (let attempt = 0; attempt < 3; attempt++) {
 185→      try {
 186→        const res = await fetch(config.submitUrl, {
 187→          method: "POST",
 188→          headers: { "Content-Type": "application/json" },
 189→          body: payload,
 190→        });
 191→        if (res.ok) { saved = true; break; }
 192→      } catch { /* retry */ }
 193→      if (attempt < 2) await new Promise((r) => setTimeout(r, 1000 * (attempt + 1)));
 194→    }
 195→    if (!saved) throw new Error("SUBMIT_FAILED");
 196→  }
 197→}
