 280→function renderWithLinks(text) {
 281→  const urlRegex = /(https?:\/\/[^\s]+)/g;
 282→  const parts = text.split(urlRegex);
 283→  return parts.map((part, i) =>
 284→    urlRegex.test(part)
 285→      ? <a key={i} href={part} target="_blank" rel="noopener noreferrer" style={{ color: s.accent, textDecoration: "underline" }}>{part}</a>
 286→      : part
 287→  );
 288→}
