 296→    }}>
 297→      <div style={{ fontSize: 15, fontWeight: 700, color: s.accent, letterSpacing: "0.02em" }}>
 298→        {title}
 299→      </div>
 300→      {desc && <div style={{ fontSize: 14, color: s.textSub, marginTop: 6, lineHeight: 1.6, whiteSpace: "pre-line" }}>{renderWithLinks(desc)}</div>}
 301→    </div>
