 305→// ─── Input controls ───
 306→const inputBase = {
 307→  flex: 1, padding: "14px 18px", borderRadius: 24,
 308→  background: "#fff", border: "1px solid rgba(0,0,0,0.12)",
 309→  color: s.text, fontSize: 16, fontFamily: "inherit", outline: "none",
 310→  resize: "none", minHeight: 48, lineHeight: 1.5, transition: "all 0.2s",
 311→};
 312→const focusStyle = {
 313→  background: "#fff",
 314→  borderColor: "rgba(0,49,216,0.4)",
 315→  boxShadow: "0 0 0 3px rgba(0,49,216,0.15)",
 316→};
