 318→function TextInput({ q, onSubmit, initialValue }) {
 319→  const [v, setV] = useState(initialValue || "");
 320→  const [focused, setFocused] = useState(false);
 321→  const ref = useRef(null);
 322→  const ta = q.type === "textarea";
 323→
 324→  useEffect(() => { ref.current?.focus(); }, []);
 325→
 326→  const submit = () => {
 327→    if (q.required && !v.trim()) return;
 328→    onSubmit(v.trim() || "");
 329→  };
 330→
 331→  const handleKeyDown = (e) => {
 332→    if (ta) {
 333→      // Textarea: Ctrl+Enter to submit, Enter adds newline
 334→      if (e.key === "Enter" && (e.ctrlKey || e.metaKey)) { e.preventDefault(); submit(); }
 335→    } else {
 336→      // Text input: Enter to submit
 337→      if (e.key === "Enter" && !e.shiftKey) { e.preventDefault(); submit(); }
 338→    }
 339→  };
 340→
 341→  const Tag = ta ? "textarea" : "input";
 342→  return (
 343→    <div>
 344→      <div style={{ display: "flex", gap: 10, alignItems: "flex-end" }}>
 345→        <Tag
 346→          ref={ref} value={v} rows={ta ? 3 : undefined} type={ta ? undefined : "text"}
 347→          placeholder={q.placeholder || "入力してください..."}
 348→          onChange={(e) => setV(e.target.value)}
 349→          onKeyDown={handleKeyDown}
 350→          onFocus={() => setFocused(true)} onBlur={() => setFocused(false)}
 351→          style={{ ...inputBase, ...(ta && { maxHeight: 140 }), ...(focused ? focusStyle : {}) }}
 352→        />
 353→        <button onClick={submit} disabled={q.required && !v.trim()} style={{
 354→          width: 48, height: 48, borderRadius: 24, border: "none",
 355→          background: s.accent, color: "#fff", cursor: "pointer",
 356→          display: "flex", alignItems: "center", justifyContent: "center",
 357→          opacity: q.required && !v.trim() ? 0.3 : 1, flexShrink: 0, transition: "all 0.15s",
 358→        }}>
 359→          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round"><line x1="22" y1="2" x2="11" y2="13"/><polygon points="22 2 15 22 11 13 2 9 22 2"/></svg>
 360→        </button>
 361→      </div>
 362→      <div style={{ fontSize: 12, color: s.textDim, marginTop: 6, paddingLeft: 4 }}>
 363→        {ta ? "Ctrl+Enter\u3067送信" : "Enter\u3067送信"}{!q.required && " / スキップ可"}
 364→      </div>
 365→    </div>
 366→  );
 367→}
