 369→// Strip example text like （例：Midjourney, DALL·E...） from pill display
 370→function shortLabel(text) {
 371→  return text.replace(/[（(]例[：:].*?[）)]/g, "").replace(/[（(][^）)]*[）)]\s*$/, "").trim();
 372→}
 373→
 374→// Split "主テキスト（補足）" into { main, note }
 375→function splitAnnotation(text) {
 376→  const m = text.match(/^(.+?)\s*[（(](.+?)[）)]$/);
 377→  return m ? { main: m[1].trim(), note: m[2] } : { main: text, note: null };
 378→}
