 380→const choiceBtnStyle = (selected) => ({
 381→  padding: "10px 18px", borderRadius: 20,
 382→  background: selected ? "rgba(0,49,216,0.12)" : "#fff",
 383→  border: `1.5px solid ${selected ? "rgba(0,49,216,0.4)" : "rgba(0,0,0,0.12)"}`,
 384→  color: selected ? s.accent : s.text,
 385→  fontSize: 15, fontFamily: "inherit", cursor: "pointer",
 386→  transition: "all 0.15s", textAlign: "left", lineHeight: 1.4,
