 393→    <div style={{ display: "flex", flexDirection: "column", gap: 8 }}>
 394→      {q.options.map((o) => {
 395→        const { main, note } = splitAnnotation(o);
 396→        return (
 397→          <button key={o} onClick={() => onSubmit(o)} className="lgf-choice" style={{
 398→            ...choiceBtnStyle(false), display: "flex", alignItems: "center", gap: 10, width: "100%",
 399→          }}>
 400→            <span>{main}</span>
 401→            {note && <span style={{ fontSize: 12, color: s.textDim, fontWeight: 400 }}>{note}</span>}
 402→          </button>
 403→        );
 404→      })}
 405→    </div>
 406→  );
 407→}
