 409→function Dropdown({ q, onSubmit }) {
 410→  const [v, setV] = useState("");
 411→
 412→  return (
 413→    <div style={{ display: "flex", gap: 10, alignItems: "center" }}>
 414→      <select
 415→        value={v}
 416→        onChange={(e) => setV(e.target.value)}
 417→        style={{
 418→          flex: 1, padding: "12px 14px", borderRadius: 16,
 419→          background: "#fff", border: "1px solid rgba(0,0,0,0.12)",
 420→          color: s.text, fontSize: 16, fontFamily: "inherit",
