 427→        ))}
 428→      </select>
 429→      <button
 430→        onClick={() => v && onSubmit(v)}
 431→        disabled={q.required && !v}
 432→        style={{
 433→          width: 48, height: 48, borderRadius: 24, border: "none",
 434→          background: s.accent, color: "#fff", cursor: "pointer",
 435→          display: "flex", alignItems: "center", justifyContent: "center",
 436→          opacity: q.required && !v ? 0.3 : 1, flexShrink: 0,
 437→        }}
 438→      >
 439→        <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round"><polyline points="20 6 9 17 4 12"/></svg>
 440→      </button>
 441→    </div>
 442→  );
 443→}
 444→
 445→function MultiChoice({ q, onSubmit }) {
 446→  const [sel, setSel] = useState(new Set());
 447→  const [otherText, setOtherText] = useState("");
 448→  const toggle = (o) => setSel((p) => { const n = new Set(p); n.has(o) ? n.delete(o) : n.add(o); return n; });
 449→
 450→  const isSeparator = (o) => typeof o === "string" && o.includes("─────");
 451→  const selectableOptions = q.options.filter((o) => !isSeparator(o));
 452→  const otherOption = selectableOptions.find((o) => o.startsWith("その他"));
 453→  const otherSelected = otherOption && sel.has(otherOption);
 454→
 455→  const doSubmit = () => {
 456→    let result = Array.from(sel);
 457→    if (otherOption && sel.has(otherOption) && otherText.trim()) {
 458→      result = result.map((o) => o === otherOption ? `その他: ${otherText.trim()}` : o);
 459→    }
 460→    onSubmit(result.length > 0 ? result : []);
 461→  };
 462→
 463→  // 2-option: auto-submit on tap (only count selectable options)
 464→  if (selectableOptions.length <= 2) {
 465→    return (
 466→      <div style={choiceWrap}>
 467→        {selectableOptions.map((o) => (
 468→          <button key={o} onClick={() => onSubmit([o])} className="lgf-choice" style={choiceBtnStyle(false)}>{shortLabel(o)}</button>
 469→        ))}
 470→      </div>
 471→    );
 472→  }
 473→
 474→  // Large option sets (>15) use searchable UI with optional popularOptions
 475→  if (selectableOptions.length > 15) {
 476→    return <SearchableMulti q={q} onSubmit={onSubmit} />;
 477→  }
 478→
 479→  return (
 480→    <div>
 481→      <div style={choiceWrap}>
 482→        {q.options.map((o) => {
 483→          if (isSeparator(o)) {
 484→            return (
 485→              <div key={o} style={{ width: "100%", fontSize: 12, color: s.textDim, padding: "6px 4px 2px", fontWeight: 600 }}>
 486→                {o.replace(/─/g, "").trim()}
 487→              </div>
 488→            );
 489→          }
 490→          return (
 491→            <button key={o} onClick={() => toggle(o)} className="lgf-choice" style={choiceBtnStyle(sel.has(o))}>{shortLabel(o)}</button>
 492→          );
 493→        })}
 494→      </div>
 495→      {otherSelected && (
 496→        <div style={{ marginTop: 8 }}>
 497→          <input
 498→            type="text" value={otherText} placeholder="具体的に記入してください..."
 499→            onChange={(e) => setOtherText(e.target.value)}
 500→            onKeyDown={(e) => { if (e.key === "Enter") { e.preventDefault(); doSubmit(); } }}
 501→            autoFocus
 502→            style={{ ...inputBase, width: "100%", padding: "10px 14px", borderRadius: 16 }}
 503→          />
 504→        </div>
 505→      )}
 506→      <div style={{ display: "flex", justifyContent: "flex-end", marginTop: 12 }}>
 507→        <button
 508→          onClick={doSubmit}
 509→          disabled={q.required && sel.size === 0}
 510→          className="lgf-submit"
 511→          style={{
 512→            padding: "10px 20px", borderRadius: 20, border: "none",
 513→            background: s.accent, color: "#fff", fontSize: 14,
 514→            fontWeight: 600, cursor: "pointer", fontFamily: "inherit",
 515→            opacity: q.required && sel.size === 0 ? 0.3 : 1,
 516→          }}
 517→        >
 518→          {sel.size > 0 ? `${sel.size}件選択して次へ` : "次へ"}
 519→        </button>
 520→      </div>
 521→    </div>
 522→  );
 523→}
 524→
 525→function SearchableMulti({ q, onSubmit }) {
 526→  const [sel, setSel] = useState(new Set());
 527→  const [customItems, setCustomItems] = useState([]);
 528→  const [filter, setFilter] = useState("");
 529→  const ref = useRef(null);
 530→
 531→  useEffect(() => { ref.current?.focus(); }, []);
 532→
 533→  const toggle = (o) => setSel((p) => {
 534→    const n = new Set(p);
 535→    n.has(o) ? n.delete(o) : n.add(o);
 536→    return n;
 537→  });
 538→
 539→  const addCustom = (text) => {
 540→    const trimmed = text.trim();
 541→    if (!trimmed || sel.has(trimmed) || q.options.includes(trimmed)) return;
 542→    setCustomItems((p) => [...p, trimmed]);
 543→    setSel((p) => new Set([...p, trimmed]));
 544→  };
 545→
 546→  const lower = filter.toLowerCase();
 547→  const filtered = filter
 548→    ? q.options.filter((o) => o.toLowerCase().includes(lower))
 549→    : [];
 550→  const unselectedFiltered = filtered.filter((o) => !sel.has(o));
 551→
 552→  // Popular options — always visible, selected items removed
 553→  const popular = q.popularOptions || [];
 554→  const unselectedPopular = popular.filter((o) => !sel.has(o));
 555→
 556→  const handleKeyDown = (e) => {
 557→    if (e.key === "Enter") {
 558→      e.preventDefault();
 559→      if (unselectedFiltered.length > 0) {
 560→        // Select the first match
 561→        toggle(unselectedFiltered[0]);
 562→        setFilter("");
 563→        ref.current?.focus();
 564→      } else if (filter.trim()) {
 565→        // No match: register as custom entry
 566→        addCustom(filter);
 567→        setFilter("");
 568→        ref.current?.focus();
 569→      }
 570→    }
 571→  };
 572→
 573→  return (
 574→    <div>
 575→      {/* Popular quick-select buttons — always visible */}
 576→      {unselectedPopular.length > 0 && !filter && (
 577→        <div style={{ marginBottom: 12 }}>
 578→          <div style={{ fontSize: 12, color: s.textDim, marginBottom: 6 }}>よく選ばれています</div>
 579→          <div style={{ display: "flex", flexWrap: "wrap", gap: 6 }}>
 580→            {unselectedPopular.map((o) => (
 581→              <button key={o} onClick={() => toggle(o)} className="lgf-choice" style={{
 582→                ...choiceBtnStyle(false),
 583→                background: "rgba(0,49,216,0.06)",
 584→                borderColor: "rgba(0,49,216,0.25)",
 585→                fontWeight: 600,
 586→              }}>{shortLabel(o)}</button>
 587→            ))}
 588→          </div>
 589→        </div>
 590→      )}
 591→
 592→      {/* Selected tags */}
 593→      {sel.size > 0 && (
 594→        <div style={{ display: "flex", flexWrap: "wrap", gap: 6, marginBottom: 10 }}>
 595→          {Array.from(sel).map((o) => (
 596→            <button key={o} onClick={() => {
 597→              toggle(o);
 598→              setCustomItems((p) => p.filter((c) => c !== o));
 599→            }} style={{
 600→              padding: "5px 12px", borderRadius: 16,
 601→              background: "rgba(0,49,216,0.1)", border: "1px solid rgba(0,49,216,0.3)",
 602→              color: s.accent, fontSize: 13, fontFamily: "inherit", cursor: "pointer",
 603→              display: "flex", alignItems: "center", gap: 6,
 604→            }}>
 605→              {shortLabel(o)}
 606→              <span style={{ fontSize: 11, opacity: 0.7 }}>✕</span>
 607→            </button>
 608→          ))}
 609→        </div>
 610→      )}
 611→
 612→      {/* Search input */}
 613→      <input
 614→        ref={ref}
 615→        value={filter}
 616→        placeholder="サービス名を入力（Enterで追加）..."
 617→        onChange={(e) => setFilter(e.target.value)}
 618→        onKeyDown={handleKeyDown}
 619→        style={{
 620→          ...inputBase, width: "100%", marginBottom: 8,
 621→          padding: "12px 16px", borderRadius: 20,
 622→        }}
 623→      />
 624→
 625→      {/* Matching options */}
 626→      {filter && (
 627→        <div style={{
 628→          maxHeight: 200, overflowY: "auto",
 629→          display: "flex", flexWrap: "wrap", gap: 6, marginBottom: 8,
 630→        }}>
 631→          {unselectedFiltered.length > 0 ? (
 632→            unselectedFiltered.map((o) => (
 633→              <button key={o} onClick={() => { toggle(o); setFilter(""); ref.current?.focus(); }} className="lgf-choice" style={choiceBtnStyle(false)}>
 634→                {shortLabel(o)}
 635→              </button>
 636→            ))
 637→          ) : (
 638→            <div style={{ fontSize: 13, color: s.textDim, padding: "8px 4px" }}>
 639→              該当なし — Enterで「{filter}」を追加
 640→            </div>
 641→          )}
 642→        </div>
 643→      )}
 644→
 645→      {/* Hint + submit */}
 646→      {!filter && sel.size === 0 && unselectedPopular.length === 0 && (
 647→        <div style={{ fontSize: 12, color: s.textDim, marginBottom: 8, paddingLeft: 4 }}>
 648→          {q.options.length}件の候補から検索できます
 649→        </div>
 650→      )}
 651→
 652→      <div style={{ display: "flex", justifyContent: "flex-end" }}>
 653→        <button
 654→          onClick={() => onSubmit(sel.size > 0 ? Array.from(sel) : [])}
 655→          disabled={q.required && sel.size === 0}
 656→          className="lgf-submit"
 657→          style={{
 658→            padding: "10px 20px", borderRadius: 20, border: "none",
 659→            background: s.accent, color: "#fff", fontSize: 14,
 660→            fontWeight: 600, cursor: "pointer", fontFamily: "inherit",
 661→            opacity: q.required && sel.size === 0 ? 0.3 : 1,
 662→          }}
 663→        >
 664→          {sel.size > 0 ? `${sel.size}件選択して次へ` : "次へ"}
 665→        </button>
 666→      </div>
 667→    </div>
 668→  );
 669→}
