 671→// ─── localStorage persistence (7-day TTL) ───
 672→const STORAGE_TTL_MS = 7 * 24 * 60 * 60 * 1000;
 673→
 674→function loadProgress(surveyId) {
 675→  try {
 676→    const raw = localStorage.getItem(`lgf_${surveyId}`);
 677→    if (!raw) return null;
 678→    const saved = JSON.parse(raw);
 679→    if (Date.now() - saved.ts > STORAGE_TTL_MS) {
 680→      localStorage.removeItem(`lgf_${surveyId}`);
 681→      return null;
 682→    }
 683→    return saved;
 684→  } catch { return null; }
 685→}
 686→
 687→function saveProgress(surveyId, step, answers) {
 688→  try {
 689→    localStorage.setItem(`lgf_${surveyId}`, JSON.stringify({ step, answers, ts: Date.now() }));
 690→  } catch { /* quota exceeded — ignore */ }
 691→}
 692→
 693→function clearProgress(surveyId) {
 694→  try { localStorage.removeItem(`lgf_${surveyId}`); } catch { /* ignore */ }
 695→}
 696→
 697→// ─── Main component ───
 698→export default function LiquidGlassForm({ formConfig, onComplete = null, initialEmail = "", initialBirthYear = "", surveyLabel = "" }) {
 699→  const surveyId = formConfig.sourceUrl || formConfig.title;
 700→  const saved = useRef(loadProgress(surveyId));
 701→
 702→  // Pre-fill from props (email, birth year)
 703→  const initialAnswers = (() => {
 704→    const base = saved.current?.answers || {};
 705→    if (initialEmail && formConfig.questions) {
 706→      const emailQ = formConfig.questions.find(q => q.id === "entry_1243761143");
 707→      if (emailQ && !base[emailQ.id]) {
 708→        base[emailQ.id] = initialEmail;
 709→      }
 710→    }
 711→    if (initialBirthYear && formConfig.questions) {
 712→      const birthQ = formConfig.questions.find(q => q.id === "entry_170746194");
 713→      if (birthQ && !base[birthQ.id]) {
 714→        base[birthQ.id] = initialBirthYear;
 715→      }
 716→    }
 717→    return base;
 718→  })();
 719→
 720→  const [msgs, setMsgs] = useState([]);
 721→  const [step, setStep] = useState(0);
 722→  const [answers, setAnswers] = useState({});
 723→  const [showInput, setShowInput] = useState(false);
 724→  const [phase, setPhase] = useState("chat"); // chat | complete
 725→  const chatRef = useRef(null);
 726→  const ansRef = useRef(initialAnswers);
 727→
 728→  const questions = formConfig.questions;
 729→  const effectiveTotal = countEffectiveQuestions(questions, answers);
 730→  const totalQ = effectiveTotal || questions.filter((q) => q.type !== "section").length;
 731→  const answeredCount = Object.keys(ansRef.current).length;
 732→
 733→  const scroll = useCallback(() => {
 734→    requestAnimationFrame(() => {
 735→      if (chatRef.current) chatRef.current.scrollTop = chatRef.current.scrollHeight;
 736→    });
 737→  }, []);
 738→
 739→  const addMsg = useCallback(async (type, text, delay = 500, meta) => {
 740→    const typingId = Date.now();
 741→    if (type === "bot" || type === "reaction") {
 742→      setMsgs((p) => [...p, { type: "typing", id: typingId }]);
 743→      scroll();
 744→      await new Promise((r) => setTimeout(r, type === "reaction" ? 350 : delay));
 745→    }
 746→    setMsgs((p) => [
 747→      ...p.filter((m) => m.id !== typingId),
 748→      { type, text, id: Date.now() + Math.random(), ...meta },
 749→    ]);
 750→    scroll();
 751→  }, [scroll]);
 752→
 753→  // Init — restore progress or start fresh
 754→  useEffect(() => {
 755→    (async () => {
 756→      const restored = saved.current;
 757→
 758→      // GTAG: survey start
 759→      if (typeof window !== "undefined" && window.gtag) {
 760→        window.gtag("event", "survey_start", { survey_id: surveyLabel || surveyId });
 761→      }
 762→
 763→      if (restored && Object.keys(restored.answers).length > 0) {
 764→        let i = 0;
 765→        while (i < questions.length && i < restored.step) {
 766→          const q = questions[i];
 767→          // Skip questions that should be skipped based on answers so far
 768→          if (shouldSkip(q, questions, restored.answers)) { i++; continue; }
 769→          // autoAnswer: silently skip pre-filled questions in replay
 770→          if (q.autoAnswer && restored.answers[q.id]) { i++; continue; }
 771→          if (q.type === "section") {
 772→            await addMsg("section", q, 100);
 773→          } else {
 774→            await addMsg("bot", q.question, 100, { qStep: i });
 775→            const a = restored.answers[q.id];
 776→            if (a !== undefined) {
 777→              const display = Array.isArray(a)
 778→                ? (a.length > 0 ? a.map(v => shortLabel(v)).join(", ") : "(スキップ)")
 779→                : (a ? shortLabel(a) : "(スキップ)");
 780→              await addMsg("user", display, 0);
 781→            }
 782→          }
 783→          i++;
 784→        }
 785→        // Advance past skipped sections/questions to find the next active question
 786→        while (i < questions.length) {
 787→          if (shouldSkip(questions[i], questions, restored.answers)) { i++; continue; }
 788→          // autoAnswer: silently skip pre-filled questions
 789→          if (questions[i].autoAnswer && restored.answers[questions[i].id]) { i++; continue; }
 790→          if (questions[i].type === "section") {
 791→            await addMsg("section", questions[i], 100);
 792→            i++;
 793→            continue;
 794→          }
 795→          break;
 796→        }
 797→        if (i < questions.length) {
 798→          setStep(i);
 799→          await addMsg("bot", questions[i].question, 300, { qStep: i });
 800→          setShowInput(true);
 801→        }
 802→        setAnswers(restored.answers);
 803→      } else {
 804→        let i = 0;
 805→        while (i < questions.length) {
 806→          if (shouldSkip(questions[i], questions, initialAnswers)) { i++; continue; }
 807→          // autoAnswer: silently skip pre-filled questions
 808→          if (questions[i].autoAnswer && initialAnswers[questions[i].id]) { i++; continue; }
 809→          if (questions[i].type === "section") {
 810→            await addMsg("section", questions[i], 300);
 811→            i++;
 812→            continue;
 813→          }
 814→          break;
 815→        }
 816→        if (i < questions.length) {
 817→          setStep(i);
 818→          await addMsg("bot", questions[i].question, 500, { qStep: i });
 819→          setShowInput(true);
 820→        }
 821→      }
 822→    })();
 823→  }, []); // eslint-disable-line react-hooks/exhaustive-deps
 824→
 825→  const handleAnswer = useCallback(async (answer) => {
 826→    setShowInput(false);
 827→    const q = questions[step];
 828→    const display = Array.isArray(answer)
 829→      ? (answer.length > 0 ? answer.map(a => shortLabel(a)).join(", ") : "(スキップ)")
 830→      : (answer ? shortLabel(answer) : "(スキップ)");
 831→
 832→    await addMsg("user", display, 0);
 833→
 834→    const newAns = { ...ansRef.current, [q.id]: answer };
 835→    ansRef.current = newAns;
 836→    setAnswers(newAns);
 837→
 838→    // GTAG: survey progress
 839→    if (typeof window !== "undefined" && window.gtag) {
 840→      window.gtag("event", "survey_progress", {
 841→        survey_id: surveyLabel || surveyId,
 842→        question_id: q.id,
 843→        question_index: step,
 844→        total_questions: totalQ,
 845→      });
 846→    }
 847→
 848→    let next = step + 1;
 849→
 850→    saveProgress(surveyId, next, newAns);
 851→
 852→    if (q.id !== "entry_1127213393" && answer) {
 853→      const currentAnswered = Object.keys(newAns).length;
 854→      const pct = totalQ > 0 ? currentAnswered / totalQ : 0;
 855→      const progressComment = getProgressComment(pct, currentAnswered, totalQ);
 856→      if (progressComment) {
 857→        await addMsg("reaction", progressComment);
 858→      } else {
 859→        const reaction = getLocalReaction(q, answer);
 860→        if (reaction) await addMsg("reaction", reaction);
 861→      }
 862→    }
 863→
 864→    // Advance past sections, skipped questions, and autoAnswer questions
 865→    while (next < questions.length) {
 866→      const nextQ = questions[next];
 867→      if (shouldSkip(nextQ, questions, newAns)) { next++; continue; }
 868→      // autoAnswer: silently skip pre-filled questions
 869→      if (nextQ.autoAnswer && ansRef.current[nextQ.id]) { next++; continue; }
 870→      if (nextQ.type === "section") {
 871→        await addMsg("section", nextQ, 200);
 872→        next++;
 873→        continue;
 874→      }
 875→      break;
 876→    }
 877→
 878→    if (next >= questions.length) {
 879→      await addMsg("bot", "回答を送信しています...", 400);
 880→      try {
 881→        await submitSurvey(formConfig, newAns, initialEmail);
 882→      } catch {
 883→        await addMsg("bot", "送信に失敗しました。回答データは保存されています。ページを再読み込みして再送信してください。", 0);
 884→        setShowInput(false);
 885→        return;
 886→      }
 887→      clearProgress(surveyId);
 888→      // GTAG: survey complete
 889→      if (typeof window !== "undefined" && window.gtag) {
 890→        window.gtag("event", "survey_complete", { survey_id: surveyLabel || surveyId });
 891→      }
 892→      await new Promise((r) => setTimeout(r, 600));
 893→      setPhase("complete");
 894→      if (onComplete) onComplete(newAns);
 895→    } else {
 896→      setStep(next);
 897→      await addMsg("bot", questions[next].question, 500, { qStep: next });
 898→      setShowInput(true);
 899→    }
 900→  }, [step, questions, addMsg, formConfig, onComplete, surveyId, initialEmail]);
 901→
 902→  const progress = Math.round((answeredCount / totalQ) * 100);
 903→  const currentQ = step < questions.length ? questions[step] : null;
 904→
 905→  const prevQStep = (() => {
 906→    for (let i = step - 1; i >= 0; i--) {
 907→      if (questions[i].type !== "section") return i;
 908→    }
 909→    return -1;
 910→  })();
 911→
 912→  const goBack = useCallback(() => {
 913→    if (prevQStep < 0) return;
 914→    setShowInput(false);
 915→
 916→    setMsgs((prev) => {
 917→      const idx = prev.findIndex((m) => m.qStep === prevQStep);
 918→      const retained = idx >= 0 ? prev.slice(0, idx) : prev;
 919→      return [...retained, {
 920→        type: "bot", text: questions[prevQStep].question,
 921→        id: Date.now() + Math.random(), qStep: prevQStep,
 922→      }];
 923→    });
 924→
 925→    // Keep old answer in ansRef so TextInput can use it as initialValue
 926→    // It will be overwritten when user re-answers
 927→    setStep(prevQStep);
 928→    saveProgress(surveyId, prevQStep, ansRef.current);
 929→    setShowInput(true);
 930→    scroll();
 931→  }, [prevQStep, questions, surveyId, scroll]);
 932→
 933→  const isTextType = (q) => q.type === "text" || q.type === "textarea";
 934→
 935→  const getInitialValue = (q) => {
 936→    if (initialEmail && q.id === "entry_1243761143") return initialEmail;
 937→    return ansRef.current[q.id] || "";
 938→  };
 939→
 940→  const renderInput = (q) => {
 941→    switch (q.type) {
 942→      case "text": case "textarea": return <TextInput key={step} q={q} onSubmit={handleAnswer} initialValue={getInitialValue(q)} />;
 943→      case "single_choice": return <SingleChoice key={step} q={q} onSubmit={handleAnswer} />;
 944→      case "multi_choice": return <MultiChoice key={step} q={q} onSubmit={handleAnswer} />;
 945→      case "dropdown": return <Dropdown key={step} q={q} onSubmit={handleAnswer} />;
 946→      default: return <TextInput key={step} q={q} onSubmit={handleAnswer} initialValue={getInitialValue(q)} />;
 947→    }
 948→  };
 949→
 950→  const reward = formConfig.reward || "10,000 AICUポイント";
 951→
 952→  return (
 953→    <div style={{
 954→      height: "100dvh", fontFamily: "-apple-system, BlinkMacSystemFont, 'SF Pro Display', 'Noto Sans JP', sans-serif",
 955→      background: s.bg, color: s.text, display: "flex", flexDirection: "column",
 956→      overflow: "hidden", position: "relative",
 957→    }}>
 958→      {/* Header */}
 959→      <div style={{
 960→        flexShrink: 0, display: "flex", alignItems: "center", justifyContent: "space-between",
 961→        padding: "16px 20px", borderBottom: `1px solid ${s.border}`, zIndex: 2,
 962→        background: "#fff",
 963→      }}>
 964→        <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
 965→          <span style={{
 966→            fontFamily: "'Outfit', sans-serif",
 967→            fontSize: 22, fontWeight: 800, letterSpacing: "-0.02em",
 968→            color: s.teal,
 969→          }}>
 970→            AICU
 971→          </span>
 972→          <span style={{ fontSize: 15, fontWeight: 600, color: s.text }}>
 973→            Research
 974→          </span>
 975→        </div>
 976→        <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
 977→          {surveyLabel && (
 978→            <span style={{ fontSize: 12, fontWeight: 600, color: s.textDim, letterSpacing: "0.03em" }}>
 979→              {surveyLabel}
 980→            </span>
 981→          )}
 982→          <div style={{ width: 80, height: 4, borderRadius: 2, background: "rgba(0,0,0,0.08)", overflow: "hidden" }}>
 983→            <div style={{
 984→              height: "100%", borderRadius: 2, background: s.accent,
 985→              width: `${phase === "complete" ? 100 : progress}%`,
 986→              transition: "width 0.4s cubic-bezier(0.4,0,0.2,1)",
 987→            }} />
 988→          </div>
 989→          <span style={{ fontSize: 12, color: s.textDim, fontVariantNumeric: "tabular-nums", whiteSpace: "nowrap" }}>
 990→            {phase === "complete" ? "完了" : (() => {
 991→              const est = formConfig.estimatedMinutes || 5;
 992→              const remaining = Math.max(1, Math.ceil(est * (1 - progress / 100)));
 993→              return `残り約${remaining}分`;
 994→            })()}
 995→          </span>
 996→        </div>
 997→      </div>
 998→
 999→      {/* Chat area */}
1000→      <div ref={chatRef} style={{
1001→        flex: 1, overflowY: "auto",
1002→        padding: phase === "chat" && showInput && currentQ && isTextType(currentQ)
1003→          ? "20px 16px 180px" : "20px 16px 100px",
1004→        scrollBehavior: "smooth", zIndex: 1,
1005→      }}>
1006→        {phase === "complete" ? (
1007→          <div style={{
1008→            display: "flex", flexDirection: "column", alignItems: "center",
1009→            justifyContent: "center", textAlign: "center", padding: "48px 20px",
1010→            animation: "lgf-fi 0.5s ease both", minHeight: "60vh",
1011→          }}>
1012→            <div style={{
1013→              width: 68, height: 68, borderRadius: 20,
1014→              background: "rgba(52,211,153,0.1)", border: "1px solid rgba(52,211,153,0.2)",
1015→              display: "flex", alignItems: "center", justifyContent: "center",
1016→              fontSize: 30, marginBottom: 20, color: "#34d399",
1017→            }}>&#10003;</div>
1018→            <h2 style={{ fontSize: 22, fontWeight: 700, letterSpacing: "-0.03em", marginBottom: 10, color: s.text }}>
1019→              ご回答ありがとうございました
1020→            </h2>
1021→            <p style={{ fontSize: 15, color: s.textSub, lineHeight: 1.8, maxWidth: 340 }}>
1022→              謝礼 {reward} は{" "}
1023→              <a href="https://p.aicu.jp" target="_blank" rel="noopener" style={{ color: s.accent, textDecoration: "underline", fontWeight: 600 }}>p.aicu.jp</a>
1024→              {" "}で確認できます
1025→            </p>
1026→            <p style={{ fontSize: 14, color: s.textDim, lineHeight: 1.7, maxWidth: 340, marginTop: 10 }}>
1027→              AICUマガジンやAmazonギフト券など商品交換は
1028→              <a href="https://www.aicu.blog/category/all-products" target="_blank" rel="noopener" style={{ color: s.accent, textDecoration: "underline" }}>こちら</a>
1029→            </p>
1030→            <div style={{ marginTop: 20, fontSize: 13, color: s.textDim, lineHeight: 1.6 }}>
1031→              <a href="https://corp.aicu.ai/ja/privacy" target="_blank" rel="noopener" style={{ color: s.textDim, textDecoration: "underline" }}>プライバシーポリシー</a>
1032→              {" / "}
1033→              <a href="https://www.aicu.blog/terms/plan-free" target="_blank" rel="noopener" style={{ color: s.textDim, textDecoration: "underline" }}>利用規約</a>
1034→            </div>
1035→            <div style={{
1036→              marginTop: 24, padding: "12px 22px", borderRadius: 14,
1037→              background: "rgba(0,0,0,0.03)", border: "1px solid rgba(0,0,0,0.06)",
1038→              fontSize: 13, color: s.textDim,
1039→            }}>
1040→              Powered by{" "}
1041→              <span style={{ fontFamily: "'Outfit', sans-serif", color: s.teal, fontWeight: 700 }}>AICU</span>
1042→              {" "}Research
