"use client"

import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip } from "recharts"
import MyAnswerBadge, { isMyAnswer } from "./MyAnswerBadge"
import { REGIONAL_BLOCKS, REGIONAL_BLOCK_ORDER, REGIONAL_COLORS } from "@/lib/survey-viz-config"

type Props = {
  counts: Record<string, number>
  answered: number
  myAnswer?: string | string[]
}

export default function RegionalPieChart({ counts, answered, myAnswer }: Props) {
  // Aggregate prefectures into regional blocks
  const blockCounts: Record<string, number> = {}
  for (const [pref, count] of Object.entries(counts)) {
    if (count === 0) continue
    const block = REGIONAL_BLOCKS[pref] || "その他"
    blockCounts[block] = (blockCounts[block] || 0) + count
  }

  const data = REGIONAL_BLOCK_ORDER
    .filter((b) => (blockCounts[b] || 0) > 0)
    .map((name) => ({ name, value: blockCounts[name] }))

  // Add any unlisted blocks
  for (const [name, value] of Object.entries(blockCounts)) {
    if (!REGIONAL_BLOCK_ORDER.includes(name) && value > 0) {
      data.push({ name, value })
    }
  }

  if (data.length === 0) return <div style={{ fontSize: 13, color: "#ccc" }}>回答なし</div>

  // Determine which block "my answer" falls into
  const myBlock = myAnswer && typeof myAnswer === "string" ? REGIONAL_BLOCKS[myAnswer] : undefined

  return (
    <div>
      <div style={{ width: "100%", height: 220 }}>
        <ResponsiveContainer>
          <PieChart>
            <Pie
              data={data}
              cx="50%"
              cy="50%"
              innerRadius="40%"
              outerRadius="80%"
              dataKey="value"
              stroke="rgba(255,255,255,0.6)"
              strokeWidth={2}
              label={({ name, percent }: { name?: string; percent?: number }) => (percent ?? 0) > 0.05 ? (name ?? "") : ""}
              labelLine={false}
            >
              {data.map((d, i) => (
                <Cell key={i} fill={REGIONAL_COLORS[d.name] || `hsl(${i * 40}, 60%, 55%)`} />
              ))}
            </Pie>
            <Tooltip
              formatter={(value: number) => {
                const pct = answered > 0 ? Math.round((value / answered) * 100) : 0
                return [`${value}件 (${pct}%)`, "回答数"]
              }}
              contentStyle={{ fontSize: 12, borderRadius: 8 }}
            />
          </PieChart>
        </ResponsiveContainer>
      </div>

      <div style={{ display: "flex", flexWrap: "wrap", gap: "4px 16px", marginTop: 4 }}>
        {data.map((d) => {
          const pct = answered > 0 ? Math.round((d.value / answered) * 100) : 0
          const mine = myBlock === d.name
          return (
            <div key={d.name} style={{
              display: "flex", alignItems: "center", gap: 6, fontSize: 12,
              ...(mine ? { fontWeight: 700 } : {}),
            }}>
              <span style={{
                width: 10, height: 10, borderRadius: "50%", flexShrink: 0,
                background: REGIONAL_COLORS[d.name] || "#999",
                border: mine ? "2px solid #E65100" : undefined,
              }} />
              <span style={{ color: "#333" }}>{d.name}</span>
              <span style={{ color: "#888", fontWeight: 600 }}>{d.value} ({pct}%)</span>
              {mine && <MyAnswerBadge />}
            </div>
          )
        })}
      </div>
    </div>
  )
}

"use client"

import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Legend, Cell } from "recharts"
import MyAnswerBadge, { isMyAnswer } from "./MyAnswerBadge"

type Props = {
  doneCounts: Record<string, number>
  wantCounts: Record<string, number>
  doneAnswered: number
  wantAnswered: number
  myDone?: string | string[]
  myWant?: string | string[]
}

const DONE_COLOR = "#41C9B4"
const WANT_COLOR = "#0031D8"

export default function PairedBarChart({ doneCounts, wantCounts, doneAnswered, wantAnswered, myDone, myWant }: Props) {
  // Merge all labels from both
  const allLabels = new Set([...Object.keys(doneCounts), ...Object.keys(wantCounts)])
  const data = Array.from(allLabels).map((label) => ({
    label,
    done: doneCounts[label] || 0,
    want: wantCounts[label] || 0,
    donePct: doneAnswered > 0 ? Math.round(((doneCounts[label] || 0) / doneAnswered) * 100) : 0,
    wantPct: wantAnswered > 0 ? Math.round(((wantCounts[label] || 0) / wantAnswered) * 100) : 0,
  }))

  if (data.length === 0) return <div style={{ fontSize: 13, color: "#ccc" }}>回答なし</div>

  return (
    <div>
      <div style={{ width: "100%", height: Math.max(250, data.length * 40 + 60) }}>
        <ResponsiveContainer>
          <BarChart data={data} layout="vertical" margin={{ left: 10, right: 20, top: 5, bottom: 5 }}>
            <XAxis type="number" fontSize={11} tickFormatter={(v) => `${v}`} />
            <YAxis
              type="category"
              dataKey="label"
              width={100}
              fontSize={11}
              tick={{ fill: "#333" }}
            />
            <Tooltip
              formatter={(value: number, name: string) => {
                const total = name === "done" ? doneAnswered : wantAnswered
                const pct = total > 0 ? Math.round((value / total) * 100) : 0
                return [`${value}件 (${pct}%)`, name === "done" ? "実現済み" : "期待"]
              }}
              contentStyle={{ fontSize: 12, borderRadius: 8 }}
            />
            <Legend
              formatter={(value) => value === "done" ? "実現済み" : "今後期待"}
              wrapperStyle={{ fontSize: 12 }}
            />
            <Bar dataKey="done" fill={DONE_COLOR} radius={[0, 4, 4, 0]} barSize={14}>
              {data.map((d, i) => (
                <Cell
                  key={i}
                  fill={DONE_COLOR}
                  stroke={isMyAnswer(myDone, d.label) ? "#E65100" : undefined}
                  strokeWidth={isMyAnswer(myDone, d.label) ? 2 : 0}
                />
              ))}
            </Bar>
            <Bar dataKey="want" fill={WANT_COLOR} radius={[0, 4, 4, 0]} barSize={14}>
              {data.map((d, i) => (
                <Cell
                  key={i}
                  fill={WANT_COLOR}
                  stroke={isMyAnswer(myWant, d.label) ? "#E65100" : undefined}
                  strokeWidth={isMyAnswer(myWant, d.label) ? 2 : 0}
                />
              ))}
            </Bar>
          </BarChart>
        </ResponsiveContainer>
      </div>

      {/* Gap analysis text */}
      {data.some((d) => Math.abs(d.wantPct - d.donePct) >= 10) && (
        <div style={{ marginTop: 8, fontSize: 12, color: "#666", lineHeight: 1.6 }}>
          <span style={{ fontWeight: 600, color: "#333" }}>ギャップ分析: </span>
          {data
            .filter((d) => d.wantPct - d.donePct >= 10)
            .sort((a, b) => (b.wantPct - b.donePct) - (a.wantPct - a.donePct))
            .slice(0, 3)
            .map((d) => `${d.label}(+${d.wantPct - d.donePct}pt)`)
            .join("、")}
          {" "}に期待が大きい
        </div>
      )}

      {(myDone || myWant) && (
        <div style={{ marginTop: 4 }}>
          <MyAnswerBadge /> <span style={{ fontSize: 12, color: "#888" }}>= あなたの回答</span>
        </div>
      )}
    </div>
  )
}

