   1→"use client";
   2→
   3→import { useState, useRef, useEffect, useCallback } from "react";
   4→
   5→// ─── Local reaction (no LLM API call) ───
   6→const LOCAL_REACTIONS = [
   7→  "なるほど！",
   8→  "ありがとうございます！",
   9→  "了解です！",
  10→  "OK！次いきますね",
  11→  "承知しました！",
  12→];
  13→
  14→function getLocalReaction(currentQ, answer) {
  15→  if (!answer || (Array.isArray(answer) && answer.length === 0)) return null;
  16→  const idx = Math.abs(hashCode(currentQ.id + String(answer))) % LOCAL_REACTIONS.length;
  17→  return LOCAL_REACTIONS[idx];
  18→}
  19→
  20→// Progress milestones — shown once at each threshold
  21→const PROGRESS_MILESTONES = [
  22→  { at: 0.25, text: "4分の1まで来ました！" },
  23→  { at: 0.50, text: "半分まで来ました！あと少しです" },
  24→  { at: 0.75, text: "あと4分の1！もうすぐ完了です" },
  25→  { at: 0.90, text: "あともう少しで終わりです！" },
  26→];
  27→const _shownMilestones = new Set();
  28→
  29→function getProgressComment(pct, answered, total) {
  30→  for (const m of PROGRESS_MILESTONES) {
  31→    if (pct >= m.at && !_shownMilestones.has(m.at)) {
  32→      // Check the previous answer count didn't already cross this threshold
  33→      if ((answered - 1) / total < m.at) {
  34→        _shownMilestones.add(m.at);
  35→        return m.text;
  36→      }
  37→      _shownMilestones.add(m.at); // mark as passed even if we missed it
  38→    }
  39→  }
  40→  return null;
  41→}
  42→
  43→function hashCode(str) {
  44→  let hash = 0;
  45→  for (let i = 0; i < str.length; i++) {
  46→    hash = ((hash << 5) - hash + str.charCodeAt(i)) | 0;
  47→  }
  48→  return hash;
  49→}
  50→
  51→// ─── Skip logic (conditional branching) ───
  52→function evaluateSkipIf(skipIf, answers) {
  53→  if (!skipIf) return false;
  54→  const answer = answers[skipIf.questionId];
  55→  if (answer === undefined) return false;
  56→  if (skipIf.equals !== undefined) {
  57→    return Array.isArray(answer) ? answer.includes(skipIf.equals) : answer === skipIf.equals;
  58→  }
  59→  if (skipIf.notEquals !== undefined) {
  60→    return Array.isArray(answer) ? !answer.includes(skipIf.notEquals) : answer !== skipIf.notEquals;
  61→  }
  62→  return false;
  63→}
  64→
  65→function shouldSkip(q, allQuestions, answers) {
  66→  // Direct skipIf on the question itself
  67→  if (q.skipIf) return evaluateSkipIf(q.skipIf, answers);
  68→  // Non-section questions inherit skipIf from their parent section
  69→  if (q.type !== "section") {
  70→    const idx = allQuestions.indexOf(q);
  71→    for (let i = idx - 1; i >= 0; i--) {
  72→      if (allQuestions[i].type === "section") {
  73→        return allQuestions[i].skipIf ? evaluateSkipIf(allQuestions[i].skipIf, answers) : false;
  74→      }
  75→    }
  76→  }
  77→  return false;
  78→}
  79→
  80→function countEffectiveQuestions(questions, answers) {
  81→  let count = 0;
  82→  let sectionSkipped = false;
  83→  for (const q of questions) {
  84→    if (q.type === "section") {
  85→      sectionSkipped = q.skipIf ? evaluateSkipIf(q.skipIf, answers) : false;
  86→      continue;
  87→    }
  88→    if (sectionSkipped) continue;
  89→    if (q.skipIf && evaluateSkipIf(q.skipIf, answers)) continue;
  90→    count++;
  91→  }
  92→  return count;
  93→}
  94→
  95→// ─── Submit helpers ───
  96→async function submitSurvey(config, answers, email) {
  97→  // Google Form submission
  98→  if (config.submitToGoogleForm && config.resolvedUrl) {
  99→    const submitUrl = config.resolvedUrl.replace("/viewform", "/formResponse");
 100→    const params = new URLSearchParams();
 101→    for (const q of config.questions) {
 102→      if (q.type === "section" || !q.entryId) continue;
 103→      const a = answers[q.id];
 104→      if (a === undefined || a === null || a === "") continue;
 105→      if (Array.isArray(a)) {
 106→        a.forEach((v) => params.append(`entry.${q.entryId}`, v));
 107→      } else {
 108→        params.set(`entry.${q.entryId}`, String(a));
 109→      }
 110→    }
 111→    const pageCount = config.questions.filter((q) => q.type === "section").length + 1;
 112→    params.set("pageHistory", Array.from({ length: pageCount }, (_, i) => i).join(","));
 113→    params.set("fvv", "1");
 114→    try {
 115→      await fetch(submitUrl, {
 116→        method: "POST",
 117→        mode: "no-cors",
 118→        headers: { "Content-Type": "application/x-www-form-urlencoded" },
 119→        body: params.toString(),
 120→      });
 121→    } catch { /* no-cors — ignore */ }
 122→  }
 123→  // Custom API submission (with retry)
 124→  if (config.submitUrl) {
 125→    const effectiveEmail = email || answers["entry_1243761143"] || undefined;
 126→    const payload = JSON.stringify({
 127→      surveyId: config.sourceUrl || config.title,
 128→      answers,
 129→      submittedAt: new Date().toISOString(),
 130→      email: effectiveEmail,
 131→    });
 132→    let saved = false;
 133→    for (let attempt = 0; attempt < 3; attempt++) {
 134→      try {
 135→        const res = await fetch(config.submitUrl, {
 136→          method: "POST",
 137→          headers: { "Content-Type": "application/json" },
 138→          body: payload,
 139→        });
 140→        if (res.ok) { saved = true; break; }
 141→      } catch { /* retry */ }
 142→      if (attempt < 2) await new Promise((r) => setTimeout(r, 1000 * (attempt + 1)));
 143→    }
 144→    if (!saved) throw new Error("SUBMIT_FAILED");
 145→  }
 146→}
