 229→function renderWithLinks(text) {
 230→  const urlRegex = /(https?:\/\/[^\s]+)/g;
 231→  const parts = text.split(urlRegex);
 232→  return parts.map((part, i) =>
 233→    urlRegex.test(part)
 234→      ? <a key={i} href={part} target="_blank" rel="noopener noreferrer" style={{ color: s.accent, textDecoration: "underline" }}>{part}</a>
 235→      : part
 236→  );
 237→}
