 254→// ─── Input controls ───
 255→const inputBase = {
 256→  flex: 1, padding: "14px 18px", borderRadius: 24,
 257→  background: "#fff", border: "1px solid rgba(0,0,0,0.12)",
 258→  color: s.text, fontSize: 16, fontFamily: "inherit", outline: "none",
 259→  resize: "none", minHeight: 48, lineHeight: 1.5, transition: "all 0.2s",
