 267→function TextInput({ q, onSubmit, initialValue }) {
 268→  const [v, setV] = useState(initialValue || "");
 269→  const [focused, setFocused] = useState(false);
 270→  const ref = useRef(null);
 271→  const ta = q.type === "textarea";
 272→
 273→  useEffect(() => { ref.current?.focus(); }, []);
 274→
 275→  const submit = () => {
 276→    if (q.required && !v.trim()) return;
 277→    onSubmit(v.trim() || "");
 278→  };
 279→
 280→  const handleKeyDown = (e) => {
 281→    if (ta) {
 282→      // Textarea: Ctrl+Enter to submit, Enter adds newline
 283→      if (e.key === "Enter" && (e.ctrlKey || e.metaKey)) { e.preventDefault(); submit(); }
 284→    } else {
 285→      // Text input: Enter to submit
 286→      if (e.key === "Enter" && !e.shiftKey) { e.preventDefault(); submit(); }
 287→    }
 288→  };
 289→
 290→  const Tag = ta ? "textarea" : "input";
 291→  return (
 292→    <div>
 293→      <div style={{ display: "flex", gap: 10, alignItems: "flex-end" }}>
 294→        <Tag
 295→          ref={ref} value={v} rows={ta ? 3 : undefined} type={ta ? undefined : "text"}
 296→          placeholder={q.placeholder || "入力してください..."}
 297→          onChange={(e) => setV(e.target.value)}
 298→          onKeyDown={handleKeyDown}
 299→          onFocus={() => setFocused(true)} onBlur={() => setFocused(false)}
 300→          style={{ ...inputBase, ...(ta && { maxHeight: 140 }), ...(focused ? focusStyle : {}) }}
 301→        />
 302→        <button onClick={submit} disabled={q.required && !v.trim()} style={{
 303→          width: 48, height: 48, borderRadius: 24, border: "none",
 304→          background: s.accent, color: "#fff", cursor: "pointer",
 305→          display: "flex", alignItems: "center", justifyContent: "center",
 306→          opacity: q.required && !v.trim() ? 0.3 : 1, flexShrink: 0, transition: "all 0.15s",
 307→        }}>
 308→          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round"><line x1="22" y1="2" x2="11" y2="13"/><polygon points="22 2 15 22 11 13 2 9 22 2"/></svg>
 309→        </button>
 310→      </div>
 311→      <div style={{ fontSize: 12, color: s.textDim, marginTop: 6, paddingLeft: 4 }}>
 312→        {ta ? "Ctrl+Enter\u3067送信" : "Enter\u3067送信"}{!q.required && " / スキップ可"}
 313→      </div>
 314→    </div>
 315→  );
 316→}
