 318→// Strip example text like （例：Midjourney, DALL·E...） from pill display
 319→function shortLabel(text) {
 320→  return text.replace(/[（(]例[：:].*?[）)]/g, "").replace(/[（(][^）)]*[）)]\s*$/, "").trim();
 321→}
 322→
 323→// Split "主テキスト（補足）" into { main, note }
 324→function splitAnnotation(text) {
 325→  const m = text.match(/^(.+?)\s*[（(](.+?)[）)]$/);
 326→  return m ? { main: m[1].trim(), note: m[2] } : { main: text, note: null };
 327→}
