 329→const choiceBtnStyle = (selected) => ({
 330→  padding: "10px 18px", borderRadius: 20,
 331→  background: selected ? "rgba(0,49,216,0.12)" : "#fff",
 332→  border: `1.5px solid ${selected ? "rgba(0,49,216,0.4)" : "rgba(0,0,0,0.12)"}`,
 333→  color: selected ? s.accent : s.text,
 334→  fontSize: 15, fontFamily: "inherit", cursor: "pointer",
 335→  transition: "all 0.15s", textAlign: "left", lineHeight: 1.4,
