 340→function SingleChoice({ q, onSubmit }) {
 341→  return (
 342→    <div style={{ display: "flex", flexDirection: "column", gap: 8 }}>
 343→      {q.options.map((o) => {
 344→        const { main, note } = splitAnnotation(o);
 345→        return (
 346→          <button key={o} onClick={() => onSubmit(o)} className="lgf-choice" style={{
 347→            ...choiceBtnStyle(false), display: "flex", alignItems: "center", gap: 10, width: "100%",
 348→          }}>
 349→            <span>{main}</span>
 350→            {note && <span style={{ fontSize: 12, color: s.textDim, fontWeight: 400 }}>{note}</span>}
 351→          </button>
 352→        );
 353→      })}
 354→    </div>
 355→  );
 356→}
 357→
 358→function Dropdown({ q, onSubmit }) {
 359→  const [v, setV] = useState("");
 360→
 361→  return (
 362→    <div style={{ display: "flex", gap: 10, alignItems: "center" }}>
 363→      <select
 364→        value={v}
 365→        onChange={(e) => setV(e.target.value)}
 366→        style={{
 367→          flex: 1, padding: "12px 14px", borderRadius: 16,
 368→          background: "#fff", border: "1px solid rgba(0,0,0,0.12)",
 369→          color: s.text, fontSize: 16, fontFamily: "inherit",
 370→          appearance: "auto", WebkitAppearance: "menulist",
 371→        }}
 372→      >
 373→        <option value="">選択してください</option>
 374→        {q.options.map((o) => (
 375→          <option key={o} value={o}>{o}</option>
 376→        ))}
 377→      </select>
 378→      <button
 379→        onClick={() => v && onSubmit(v)}
 380→        disabled={q.required && !v}
 381→        style={{
 382→          width: 48, height: 48, borderRadius: 24, border: "none",
 383→          background: s.accent, color: "#fff", cursor: "pointer",
 384→          display: "flex", alignItems: "center", justifyContent: "center",
 385→          opacity: q.required && !v ? 0.3 : 1, flexShrink: 0,
 386→        }}
 387→      >
 388→        <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round"><polyline points="20 6 9 17 4 12"/></svg>
 389→      </button>
 390→    </div>
 391→  );
 392→}
 393→
 394→function MultiChoice({ q, onSubmit }) {
 395→  const [sel, setSel] = useState(new Set());
 396→  const [otherText, setOtherText] = useState("");
 397→  const toggle = (o) => setSel((p) => { const n = new Set(p); n.has(o) ? n.delete(o) : n.add(o); return n; });
 398→
 399→  const otherOption = q.options.find((o) => o.startsWith("その他"));
 400→  const otherSelected = otherOption && sel.has(otherOption);
 401→
 402→  const doSubmit = () => {
 403→    let result = Array.from(sel);
 404→    if (otherOption && sel.has(otherOption) && otherText.trim()) {
 405→      result = result.map((o) => o === otherOption ? `その他: ${otherText.trim()}` : o);
 406→    }
 407→    onSubmit(result.length > 0 ? result : []);
 408→  };
 409→
 410→  // 2-option: auto-submit on tap
 411→  if (q.options.length <= 2) {
 412→    return (
 413→      <div style={choiceWrap}>
 414→        {q.options.map((o) => (
 415→          <button key={o} onClick={() => onSubmit([o])} className="lgf-choice" style={choiceBtnStyle(false)}>{shortLabel(o)}</button>
 416→        ))}
 417→      </div>
 418→    );
 419→  }
 420→
 421→  // Large option sets (>15) use searchable UI with optional popularOptions
 422→  if (q.options.length > 15) {
 423→    return <SearchableMulti q={q} onSubmit={onSubmit} />;
 424→  }
 425→
 426→  return (
 427→    <div>
 428→      <div style={choiceWrap}>
 429→        {q.options.map((o) => (
 430→          <button key={o} onClick={() => toggle(o)} className="lgf-choice" style={choiceBtnStyle(sel.has(o))}>{shortLabel(o)}</button>
 431→        ))}
 432→      </div>
 433→      {otherSelected && (
 434→        <div style={{ marginTop: 8 }}>
 435→          <input
 436→            type="text" value={otherText} placeholder="具体的に記入してください..."
 437→            onChange={(e) => setOtherText(e.target.value)}
 438→            onKeyDown={(e) => { if (e.key === "Enter") { e.preventDefault(); doSubmit(); } }}
 439→            autoFocus
 440→            style={{ ...inputBase, width: "100%", padding: "10px 14px", borderRadius: 16 }}
 441→          />
 442→        </div>
 443→      )}
 444→      <div style={{ display: "flex", justifyContent: "flex-end", marginTop: 12 }}>
 445→        <button
 446→          onClick={doSubmit}
 447→          disabled={q.required && sel.size === 0}
 448→          className="lgf-submit"
 449→          style={{
 450→            padding: "10px 20px", borderRadius: 20, border: "none",
 451→            background: s.accent, color: "#fff", fontSize: 14,
 452→            fontWeight: 600, cursor: "pointer", fontFamily: "inherit",
 453→            opacity: q.required && sel.size === 0 ? 0.3 : 1,
 454→          }}
 455→        >
 456→          {sel.size > 0 ? `${sel.size}件選択して次へ` : "次へ"}
 457→        </button>
 458→      </div>
 459→    </div>
 460→  );
 461→}
 462→
 463→function SearchableMulti({ q, onSubmit }) {
 464→  const [sel, setSel] = useState(new Set());
 465→  const [customItems, setCustomItems] = useState([]);
 466→  const [filter, setFilter] = useState("");
 467→  const ref = useRef(null);
 468→
 469→  useEffect(() => { ref.current?.focus(); }, []);
 470→
 471→  const toggle = (o) => setSel((p) => {
 472→    const n = new Set(p);
 473→    n.has(o) ? n.delete(o) : n.add(o);
 474→    return n;
 475→  });
 476→
 477→  const addCustom = (text) => {
 478→    const trimmed = text.trim();
 479→    if (!trimmed || sel.has(trimmed) || q.options.includes(trimmed)) return;
 480→    setCustomItems((p) => [...p, trimmed]);
 481→    setSel((p) => new Set([...p, trimmed]));
 482→  };
 483→
 484→  const lower = filter.toLowerCase();
 485→  const filtered = filter
 486→    ? q.options.filter((o) => o.toLowerCase().includes(lower))
 487→    : [];
 488→  const unselectedFiltered = filtered.filter((o) => !sel.has(o));
 489→
 490→  // Popular options — always visible, selected items removed
 491→  const popular = q.popularOptions || [];
 492→  const unselectedPopular = popular.filter((o) => !sel.has(o));
 493→
 494→  const handleKeyDown = (e) => {
 495→    if (e.key === "Enter") {
 496→      e.preventDefault();
 497→      if (unselectedFiltered.length > 0) {
 498→        // Select the first match
 499→        toggle(unselectedFiltered[0]);
 500→        setFilter("");
 501→        ref.current?.focus();
 502→      } else if (filter.trim()) {
 503→        // No match: register as custom entry
 504→        addCustom(filter);
 505→        setFilter("");
 506→        ref.current?.focus();
 507→      }
 508→    }
 509→  };
 510→
 511→  return (
 512→    <div>
 513→      {/* Popular quick-select buttons — always visible */}
 514→      {unselectedPopular.length > 0 && !filter && (
 515→        <div style={{ marginBottom: 12 }}>
 516→          <div style={{ fontSize: 12, color: s.textDim, marginBottom: 6 }}>よく選ばれています</div>
 517→          <div style={{ display: "flex", flexWrap: "wrap", gap: 6 }}>
 518→            {unselectedPopular.map((o) => (
 519→              <button key={o} onClick={() => toggle(o)} className="lgf-choice" style={{
 520→                ...choiceBtnStyle(false),
 521→                background: "rgba(0,49,216,0.06)",
 522→                borderColor: "rgba(0,49,216,0.25)",
 523→                fontWeight: 600,
 524→              }}>{shortLabel(o)}</button>
 525→            ))}
 526→          </div>
 527→        </div>
 528→      )}
 529→
 530→      {/* Selected tags */}
 531→      {sel.size > 0 && (
 532→        <div style={{ display: "flex", flexWrap: "wrap", gap: 6, marginBottom: 10 }}>
 533→          {Array.from(sel).map((o) => (
 534→            <button key={o} onClick={() => {
 535→              toggle(o);
 536→              setCustomItems((p) => p.filter((c) => c !== o));
 537→            }} style={{
 538→              padding: "5px 12px", borderRadius: 16,
 539→              background: "rgba(0,49,216,0.1)", border: "1px solid rgba(0,49,216,0.3)",
 540→              color: s.accent, fontSize: 13, fontFamily: "inherit", cursor: "pointer",
 541→              display: "flex", alignItems: "center", gap: 6,
 542→            }}>
 543→              {shortLabel(o)}
 544→              <span style={{ fontSize: 11, opacity: 0.7 }}>✕</span>
 545→            </button>
 546→          ))}
 547→        </div>
 548→      )}
 549→
 550→      {/* Search input */}
 551→      <input
 552→        ref={ref}
 553→        value={filter}
 554→        placeholder="サービス名を入力（Enterで追加）..."
 555→        onChange={(e) => setFilter(e.target.value)}
 556→        onKeyDown={handleKeyDown}
 557→        style={{
 558→          ...inputBase, width: "100%", marginBottom: 8,
 559→          padding: "12px 16px", borderRadius: 20,
 560→        }}
 561→      />
 562→
 563→      {/* Matching options */}
 564→      {filter && (
 565→        <div style={{
 566→          maxHeight: 200, overflowY: "auto",
 567→          display: "flex", flexWrap: "wrap", gap: 6, marginBottom: 8,
 568→        }}>
 569→          {unselectedFiltered.length > 0 ? (
 570→            unselectedFiltered.map((o) => (
 571→              <button key={o} onClick={() => { toggle(o); setFilter(""); ref.current?.focus(); }} className="lgf-choice" style={choiceBtnStyle(false)}>
 572→                {shortLabel(o)}
 573→              </button>
 574→            ))
 575→          ) : (
 576→            <div style={{ fontSize: 13, color: s.textDim, padding: "8px 4px" }}>
 577→              該当なし — Enterで「{filter}」を追加
 578→            </div>
 579→          )}
 580→        </div>
 581→      )}
 582→
 583→      {/* Hint + submit */}
 584→      {!filter && sel.size === 0 && unselectedPopular.length === 0 && (
 585→        <div style={{ fontSize: 12, color: s.textDim, marginBottom: 8, paddingLeft: 4 }}>
 586→          {q.options.length}件の候補から検索できます
 587→        </div>
 588→      )}
 589→
 590→      <div style={{ display: "flex", justifyContent: "flex-end" }}>
 591→        <button
 592→          onClick={() => onSubmit(sel.size > 0 ? Array.from(sel) : [])}
 593→          disabled={q.required && sel.size === 0}
 594→          className="lgf-submit"
 595→          style={{
 596→            padding: "10px 20px", borderRadius: 20, border: "none",
 597→            background: s.accent, color: "#fff", fontSize: 14,
 598→            fontWeight: 600, cursor: "pointer", fontFamily: "inherit",
 599→            opacity: q.required && sel.size === 0 ? 0.3 : 1,
 600→          }}
 601→        >
 602→          {sel.size > 0 ? `${sel.size}件選択して次へ` : "次へ"}
 603→        </button>
 604→      </div>
 605→    </div>
 606→  );
 607→}
 608→
 609→// ─── localStorage persistence (7-day TTL) ───
 610→const STORAGE_TTL_MS = 7 * 24 * 60 * 60 * 1000;
 611→
 612→function loadProgress(surveyId) {
 613→  try {
 614→    const raw = localStorage.getItem(`lgf_${surveyId}`);
 615→    if (!raw) return null;
 616→    const saved = JSON.parse(raw);
 617→    if (Date.now() - saved.ts > STORAGE_TTL_MS) {
 618→      localStorage.removeItem(`lgf_${surveyId}`);
 619→      return null;
 620→    }
 621→    return saved;
 622→  } catch { return null; }
 623→}
 624→
 625→function saveProgress(surveyId, step, answers) {
 626→  try {
 627→    localStorage.setItem(`lgf_${surveyId}`, JSON.stringify({ step, answers, ts: Date.now() }));
 628→  } catch { /* quota exceeded — ignore */ }
 629→}
 630→
 631→function clearProgress(surveyId) {
 632→  try { localStorage.removeItem(`lgf_${surveyId}`); } catch { /* ignore */ }
 633→}
 634→
 635→// ─── Main component ───
 636→export default function LiquidGlassForm({ formConfig, onComplete = null, initialEmail = "", initialBirthYear = "", surveyLabel = "" }) {
 637→  const surveyId = formConfig.sourceUrl || formConfig.title;
 638→  const saved = useRef(loadProgress(surveyId));
 639→
 640→  // Pre-fill from props (email, birth year)
 641→  const initialAnswers = (() => {
 642→    const base = saved.current?.answers || {};
 643→    if (initialEmail && formConfig.questions) {
 644→      const emailQ = formConfig.questions.find(q => q.id === "entry_1243761143");
 645→      if (emailQ && !base[emailQ.id]) {
 646→        base[emailQ.id] = initialEmail;
 647→      }
 648→    }
 649→    if (initialBirthYear && formConfig.questions) {
 650→      const birthQ = formConfig.questions.find(q => q.id === "entry_170746194");
 651→      if (birthQ && !base[birthQ.id]) {
 652→        base[birthQ.id] = initialBirthYear;
 653→      }
 654→    }
 655→    return base;
 656→  })();
 657→
 658→  const [msgs, setMsgs] = useState([]);
 659→  const [step, setStep] = useState(0);
 660→  const [answers, setAnswers] = useState({});
 661→  const [showInput, setShowInput] = useState(false);
 662→  const [phase, setPhase] = useState("chat"); // chat | complete
 663→  const chatRef = useRef(null);
 664→  const ansRef = useRef(initialAnswers);
 665→
 666→  const questions = formConfig.questions;
 667→  const effectiveTotal = countEffectiveQuestions(questions, answers);
 668→  const totalQ = effectiveTotal || questions.filter((q) => q.type !== "section").length;
 669→  const answeredCount = Object.keys(ansRef.current).length;
 670→
 671→  const scroll = useCallback(() => {
 672→    requestAnimationFrame(() => {
 673→      if (chatRef.current) chatRef.current.scrollTop = chatRef.current.scrollHeight;
 674→    });
 675→  }, []);
 676→
 677→  const addMsg = useCallback(async (type, text, delay = 500, meta) => {
 678→    const typingId = Date.now();
 679→    if (type === "bot" || type === "reaction") {
 680→      setMsgs((p) => [...p, { type: "typing", id: typingId }]);
 681→      scroll();
 682→      await new Promise((r) => setTimeout(r, type === "reaction" ? 350 : delay));
 683→    }
 684→    setMsgs((p) => [
 685→      ...p.filter((m) => m.id !== typingId),
 686→      { type, text, id: Date.now() + Math.random(), ...meta },
 687→    ]);
 688→    scroll();
 689→  }, [scroll]);
 690→
 691→  // Init — restore progress or start fresh
 692→  useEffect(() => {
 693→    (async () => {
 694→      const restored = saved.current;
 695→
 696→      // GTAG: survey start
 697→      if (typeof window !== "undefined" && window.gtag) {
 698→        window.gtag("event", "survey_start", { survey_id: surveyLabel || surveyId });
 699→      }
 700→
 701→      if (restored && Object.keys(restored.answers).length > 0) {
 702→        let i = 0;
 703→        while (i < questions.length && i < restored.step) {
 704→          const q = questions[i];
 705→          // Skip questions that should be skipped based on answers so far
 706→          if (shouldSkip(q, questions, restored.answers)) { i++; continue; }
 707→          if (q.type === "section") {
 708→            await addMsg("section", q, 100);
 709→          } else {
 710→            await addMsg("bot", q.question, 100, { qStep: i });
 711→            const a = restored.answers[q.id];
 712→            if (a !== undefined) {
 713→              const display = Array.isArray(a)
 714→                ? (a.length > 0 ? a.map(v => shortLabel(v)).join(", ") : "(スキップ)")
 715→                : (a ? shortLabel(a) : "(スキップ)");
 716→              await addMsg("user", display, 0);
 717→            }
 718→          }
 719→          i++;
 720→        }
 721→        // Advance past skipped sections/questions to find the next active question
 722→        while (i < questions.length) {
 723→          if (shouldSkip(questions[i], questions, restored.answers)) { i++; continue; }
 724→          if (questions[i].type === "section") {
 725→            await addMsg("section", questions[i], 100);
 726→            i++;
 727→            continue;
 728→          }
 729→          break;
 730→        }
 731→        if (i < questions.length) {
 732→          setStep(i);
 733→          await addMsg("bot", questions[i].question, 300, { qStep: i });
 734→          setShowInput(true);
 735→        }
 736→        setAnswers(restored.answers);
 737→      } else {
 738→        let i = 0;
 739→        while (i < questions.length) {
 740→          if (shouldSkip(questions[i], questions, {})) { i++; continue; }
 741→          if (questions[i].type === "section") {
 742→            await addMsg("section", questions[i], 300);
 743→            i++;
 744→            continue;
 745→          }
 746→          break;
 747→        }
 748→        if (i < questions.length) {
 749→          setStep(i);
 750→          await addMsg("bot", questions[i].question, 500, { qStep: i });
 751→          setShowInput(true);
 752→        }
 753→      }
 754→    })();
 755→  }, []); // eslint-disable-line react-hooks/exhaustive-deps
 756→
 757→  const handleAnswer = useCallback(async (answer) => {
 758→    setShowInput(false);
 759→    const q = questions[step];
 760→    const display = Array.isArray(answer)
 761→      ? (answer.length > 0 ? answer.map(a => shortLabel(a)).join(", ") : "(スキップ)")
 762→      : (answer ? shortLabel(answer) : "(スキップ)");
 763→
 764→    await addMsg("user", display, 0);
 765→
 766→    const newAns = { ...ansRef.current, [q.id]: answer };
 767→    ansRef.current = newAns;
 768→    setAnswers(newAns);
 769→
 770→    // GTAG: survey progress
 771→    if (typeof window !== "undefined" && window.gtag) {
 772→      window.gtag("event", "survey_progress", {
 773→        survey_id: surveyLabel || surveyId,
 774→        question_id: q.id,
 775→        question_index: step,
 776→        total_questions: totalQ,
 777→      });
 778→    }
 779→
 780→    let next = step + 1;
 781→
 782→    saveProgress(surveyId, next, newAns);
 783→
 784→    if (q.id !== "entry_1127213393" && answer) {
 785→      const currentAnswered = Object.keys(newAns).length;
 786→      const pct = totalQ > 0 ? currentAnswered / totalQ : 0;
 787→      const progressComment = getProgressComment(pct, currentAnswered, totalQ);
 788→      if (progressComment) {
 789→        await addMsg("reaction", progressComment);
 790→      } else {
 791→        const reaction = getLocalReaction(q, answer);
 792→        if (reaction) await addMsg("reaction", reaction);
 793→      }
 794→    }
 795→
 796→    // Advance past sections and skipped questions
 797→    while (next < questions.length) {
 798→      const nextQ = questions[next];
 799→      if (shouldSkip(nextQ, questions, newAns)) { next++; continue; }
 800→      if (nextQ.type === "section") {
 801→        await addMsg("section", nextQ, 200);
 802→        next++;
 803→        continue;
 804→      }
 805→      break;
 806→    }
 807→
 808→    if (next >= questions.length) {
 809→      await addMsg("bot", "回答を送信しています...", 400);
 810→      try {
 811→        await submitSurvey(formConfig, newAns, initialEmail);
 812→      } catch {
 813→        await addMsg("bot", "送信に失敗しました。回答データは保存されています。ページを再読み込みして再送信してください。", 0);
 814→        setShowInput(false);
 815→        return;
 816→      }
 817→      clearProgress(surveyId);
 818→      // GTAG: survey complete
 819→      if (typeof window !== "undefined" && window.gtag) {
 820→        window.gtag("event", "survey_complete", { survey_id: surveyLabel || surveyId });
 821→      }
 822→      await new Promise((r) => setTimeout(r, 600));
 823→      setPhase("complete");
 824→      if (onComplete) onComplete(newAns);
 825→    } else {
 826→      setStep(next);
 827→      await addMsg("bot", questions[next].question, 500, { qStep: next });
 828→      setShowInput(true);
 829→    }
 830→  }, [step, questions, addMsg, formConfig, onComplete, surveyId, initialEmail]);
 831→
 832→  const progress = Math.round((answeredCount / totalQ) * 100);
 833→  const currentQ = step < questions.length ? questions[step] : null;
 834→
 835→  const prevQStep = (() => {
 836→    for (let i = step - 1; i >= 0; i--) {
 837→      if (questions[i].type !== "section") return i;
 838→    }
 839→    return -1;
 840→  })();
 841→
 842→  const goBack = useCallback(() => {
 843→    if (prevQStep < 0) return;
 844→    setShowInput(false);
 845→
 846→    setMsgs((prev) => {
 847→      const idx = prev.findIndex((m) => m.qStep === prevQStep);
 848→      const retained = idx >= 0 ? prev.slice(0, idx) : prev;
 849→      return [...retained, {
 850→        type: "bot", text: questions[prevQStep].question,
 851→        id: Date.now() + Math.random(), qStep: prevQStep,
 852→      }];
 853→    });
 854→
 855→    // Keep old answer in ansRef so TextInput can use it as initialValue
 856→    // It will be overwritten when user re-answers
 857→    setStep(prevQStep);
 858→    saveProgress(surveyId, prevQStep, ansRef.current);
 859→    setShowInput(true);
 860→    scroll();
 861→  }, [prevQStep, questions, surveyId, scroll]);
 862→
 863→  const isTextType = (q) => q.type === "text" || q.type === "textarea";
 864→
 865→  const getInitialValue = (q) => {
 866→    if (initialEmail && q.id === "entry_1243761143") return initialEmail;
 867→    return ansRef.current[q.id] || "";
 868→  };
 869→
 870→  const renderInput = (q) => {
 871→    switch (q.type) {
 872→      case "text": case "textarea": return <TextInput key={step} q={q} onSubmit={handleAnswer} initialValue={getInitialValue(q)} />;
 873→      case "single_choice": return <SingleChoice key={step} q={q} onSubmit={handleAnswer} />;
 874→      case "multi_choice": return <MultiChoice key={step} q={q} onSubmit={handleAnswer} />;
 875→      case "dropdown": return <Dropdown key={step} q={q} onSubmit={handleAnswer} />;
 876→      default: return <TextInput key={step} q={q} onSubmit={handleAnswer} initialValue={getInitialValue(q)} />;
 877→    }
 878→  };
 879→
 880→  const reward = formConfig.reward || "10,000 AICUポイント";
 881→
 882→  return (
 883→    <div style={{
 884→      height: "100dvh", fontFamily: "-apple-system, BlinkMacSystemFont, 'SF Pro Display', 'Noto Sans JP', sans-serif",
 885→      background: s.bg, color: s.text, display: "flex", flexDirection: "column",
 886→      overflow: "hidden", position: "relative",
 887→    }}>
 888→      {/* Header */}
 889→      <div style={{
 890→        flexShrink: 0, display: "flex", alignItems: "center", justifyContent: "space-between",
 891→        padding: "16px 20px", borderBottom: `1px solid ${s.border}`, zIndex: 2,
 892→        background: "#fff",
 893→      }}>
 894→        <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
 895→          <span style={{
 896→            fontFamily: "'Outfit', sans-serif",
 897→            fontSize: 22, fontWeight: 800, letterSpacing: "-0.02em",
 898→            color: s.teal,
 899→          }}>
 900→            AICU
 901→          </span>
 902→          <span style={{ fontSize: 15, fontWeight: 600, color: s.text }}>
 903→            Research
 904→          </span>
 905→        </div>
 906→        <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
 907→          {surveyLabel && (
 908→            <span style={{ fontSize: 12, fontWeight: 600, color: s.textDim, letterSpacing: "0.03em" }}>
 909→              {surveyLabel}
 910→            </span>
 911→          )}
 912→          <div style={{ width: 80, height: 4, borderRadius: 2, background: "rgba(0,0,0,0.08)", overflow: "hidden" }}>
 913→            <div style={{
 914→              height: "100%", borderRadius: 2, background: s.accent,
 915→              width: `${phase === "complete" ? 100 : progress}%`,
 916→              transition: "width 0.4s cubic-bezier(0.4,0,0.2,1)",
 917→            }} />
 918→          </div>
 919→          <span style={{ fontSize: 12, color: s.textDim, fontVariantNumeric: "tabular-nums", whiteSpace: "nowrap" }}>
 920→            {phase === "complete" ? "完了" : (() => {
 921→              const est = formConfig.estimatedMinutes || 5;
 922→              const remaining = Math.max(1, Math.ceil(est * (1 - progress / 100)));
 923→              return `残り約${remaining}分`;
 924→            })()}
 925→          </span>
 926→        </div>
 927→      </div>
 928→
 929→      {/* Chat area */}
 930→      <div ref={chatRef} style={{
 931→        flex: 1, overflowY: "auto",
 932→        padding: phase === "chat" && showInput && currentQ && isTextType(currentQ)
 933→          ? "20px 16px 180px" : "20px 16px 100px",
 934→        scrollBehavior: "smooth", zIndex: 1,
 935→      }}>
 936→        {phase === "complete" ? (
 937→          <div style={{
 938→            display: "flex", flexDirection: "column", alignItems: "center",
 939→            justifyContent: "center", textAlign: "center", padding: "48px 20px",
 940→            animation: "lgf-fi 0.5s ease both", minHeight: "60vh",
 941→          }}>
 942→            <div style={{
 943→              width: 68, height: 68, borderRadius: 20,
 944→              background: "rgba(52,211,153,0.1)", border: "1px solid rgba(52,211,153,0.2)",
 945→              display: "flex", alignItems: "center", justifyContent: "center",
 946→              fontSize: 30, marginBottom: 20, color: "#34d399",
 947→            }}>&#10003;</div>
 948→            <h2 style={{ fontSize: 22, fontWeight: 700, letterSpacing: "-0.03em", marginBottom: 10, color: s.text }}>
 949→              ご回答ありがとうございました
 950→            </h2>
 951→            <p style={{ fontSize: 15, color: s.textSub, lineHeight: 1.8, maxWidth: 340 }}>
 952→              謝礼 {reward} は{" "}
 953→              <a href="https://p.aicu.jp" target="_blank" rel="noopener" style={{ color: s.accent, textDecoration: "underline", fontWeight: 600 }}>p.aicu.jp</a>
 954→              {" "}で確認できます
 955→            </p>
 956→            <p style={{ fontSize: 14, color: s.textDim, lineHeight: 1.7, maxWidth: 340, marginTop: 10 }}>
 957→              AICUマガジンやAmazonギフト券など商品交換は
 958→              <a href="https://www.aicu.blog/category/all-products" target="_blank" rel="noopener" style={{ color: s.accent, textDecoration: "underline" }}>こちら</a>
 959→            </p>
 960→            <div style={{ marginTop: 20, fontSize: 13, color: s.textDim, lineHeight: 1.6 }}>
 961→              <a href="https://corp.aicu.ai/ja/privacy" target="_blank" rel="noopener" style={{ color: s.textDim, textDecoration: "underline" }}>プライバシーポリシー</a>
 962→              {" / "}
 963→              <a href="https://www.aicu.blog/terms/plan-free" target="_blank" rel="noopener" style={{ color: s.textDim, textDecoration: "underline" }}>利用規約</a>
 964→            </div>
 965→            <div style={{
 966→              marginTop: 24, padding: "12px 22px", borderRadius: 14,
 967→              background: "rgba(0,0,0,0.03)", border: "1px solid rgba(0,0,0,0.06)",
 968→              fontSize: 13, color: s.textDim,
 969→            }}>
 970→              Powered by{" "}
 971→              <span style={{ fontFamily: "'Outfit', sans-serif", color: s.teal, fontWeight: 700 }}>AICU</span>
 972→              {" "}Research
 973→            </div>
 974→          </div>
 975→        ) : (<>
 976→          {msgs.map((m) => {
 977→            if (m.type === "typing") return <Bot key={m.id} text="" typing />;
 978→            if (m.type === "bot") {
 979→              const isPrev = showInput && m.qStep !== undefined && m.qStep === prevQStep;
 980→              return <Bot key={m.id} text={m.text} canGoBack={isPrev} onGoBack={isPrev ? goBack : undefined} />;
 981→            }
 982→            if (m.type === "reaction") return <Reaction key={m.id} text={m.text} />;
 983→            if (m.type === "user") return <User key={m.id} text={m.text} />;
 984→            if (m.type === "section") return <SectionHeader key={m.id} title={m.text.title} desc={m.text.description} />;
 985→            return null;
 986→          })}
 987→          {/* Inline choices — directly below the question bubble */}
 988→          {showInput && currentQ && !isTextType(currentQ) && (
 989→            <div style={{
 990→              marginBottom: 14, marginTop: -6,
 991→              padding: "12px 16px 16px", borderRadius: "4px 4px 20px 20px",
 992→              background: currentQ.type === "multi_choice" ? "rgba(0,49,216,0.025)" : "rgba(0,0,0,0.015)",
 993→              border: `1px solid ${currentQ.type === "multi_choice" ? "rgba(0,49,216,0.08)" : "rgba(0,0,0,0.04)"}`,
 994→              animation: "lgf-fi 0.3s ease both",
 995→            }}>
 996→              {currentQ.type === "multi_choice" && currentQ.options?.length > 2 && (
 997→                <div style={{ fontSize: 11, color: s.textDim, marginBottom: 8 }}>
 998→                  複数選択可
 999→                </div>
1000→              )}
1001→              {renderInput(currentQ)}
1002→            </div>
1003→          )}
1004→        </>)}
1005→      </div>
1006→
1007→      {/* Fixed bottom input — only for text/textarea */}
1008→      {phase === "chat" && showInput && currentQ && isTextType(currentQ) && (
1009→        <div style={{
1010→          flexShrink: 0, padding: "12px 16px 16px",
1011→          borderTop: `1px solid ${s.border}`,
